import React from 'react';
import { useSelector, useDispatch} from 'react-redux';
import { toggleTodoAction, deleteTodoAction} from '../redux';
import styled from 'styled-components';

const TodoListUL = styled.ul`
    width: 50%;
    background: #ddd;
    margin: auto;
    padding: 20px;
    color: #232323;
    list-style: none;
`;

const TodoListItem = styled.li`
    font-size: 200%;
    text-align: center;
`;

const TodoListCheckbox = styled.input`
  opacity: 0;
  z-index: 2;
  position: absolute;
  top: 0;
  width: 100%;
  height: 100%;
  margin: 0;
  cursor: pointer;

  &:focus {
    outline: none;
  }

  &:checked {
    & ~ ${checked} {
      width: calc(100% - 8px);
      height: calc(100% - 8px);
      transition: width 0.2s ease-out, height 0.2s ease-out;

      &::before {
        opacity: 1;
        transition: opacity 1s ease;
      }
    }
  }
`;

export default () => {

    const todos = useSelector((state) => state.todos);
    const dispatch = useDispatch();

    const toggleTodo = (todoId) => dispatch(toggleTodoAction(todoId));
    const deleteTodo = (todoId) => dispatch(deleteTodoAction(todoId));

    return (
        <TodoListUL>
            {todos.map((todo) => (
                <TodoListItem>
                <li key={todo.id}>
                    <TodoListCheckbox>
                    <input 
                    type="checkbox"
                    checked={todo.complete}
                    onChange={toggleTodo.bind(null, todo.id)}
                    />
                    </TodoListCheckbox>
                    <span className={todo.complete ? 'complete' : null}>{todo.name}</span>
                    <span className="delete-button" onClick={deleteTodo.bind(null, todo.id)}>X</span>
                </li>
                </TodoListItem>
            ))}
        </TodoListUL>
    )
}
